import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialState = { isLogin: false, idToken: "", email: "" };

const AuthSlice = createSlice({
  name: "Auth",
  initialState: initialState,
  reducers: {
    login(state, action) {
      (state.isLogin = true), (state.idToken = action.payload);
      localStorage.setItem("token", JSON.stringify(state.idToken));
    },
    logout(state) {
      (state.isLogin = false), (state.idToken = "");
      localStorage.removeItem("token");
    },
  },
});

const Store = configureStore({ reducer: AuthSlice.reducer });

export const AuthActions = AuthSlice.actions;

export default Store;
